$BODY$
BEGIN
    IF EXISTS (SELECT seller_id FROM auction WHERE NEW.bidder_id = auction.seller_id AND NEW.auction_id = auction.id ) THEN
        RAISE EXCEPTION 'A member cannot bid in their own auction.';
    END IF;
    RETURN NEW;
