-- Review
DROP FUNCTION IF EXISTS update_rating_insert CASCADE;
CREATE FUNCTION update_rating_insert() RETURNS TRIGGER AS
$BODY$
BEGIN
    UPDATE member
        SET rating = rating + NEW.value
        WHERE member.id = NEW.ratee_id;
    RETURN NEW;
END
$BODY$
LANGUAGE plpgsql;

